package homework;

public class loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
	//while loop
		
		int x=1; int y=10;
		while (x<=y){
		System.out.println(x);
		x++;
		}
		
		
		
		//do loop
		
		System.out.println("do loop");
		int a=1; int b=5;
		
		do {System.out.println(a);
		a++;
		}
		while (a<=b);
		

	}

}
